import React, { useContext } from "react";
import { Global } from "../App";
import Home from "./Home";

function SignUp() {
  const context = useContext(Global);

  return (
    <div className="mb-5">
      <div className="container ">
        <div className="row">
          <div className="box">
            <form
              onSubmit={(event) => {
                context.HandleSignup(event);
              }}
            >
              <div class="input-container">
                <input
                  type="text"
                  value={context.input1.name}
                  onChange={context.HandleInput1}
                  required
                  name="name"
                />
                <label>Full Name</label>
              </div>
              <div className="input-container">
                <input
                  type="mail"
                  name="email"
                  required
                  onChange={context.HandleInput1}
                  value={context.input1.email}
                />
                <label>Email</label>
              </div>
              <div className="input-container">
                <input
                  name="password"
                  type="password"
                  required
                  onChange={context.HandleInput1}
                  value={context.input1.password}
                />
                <label>Password</label>
              </div>
              <button type="submit" className="btn">
                signup
              </button>
            </form>
          </div>
        </div>
        <br />
      </div>
    </div>
  );
}

export default SignUp;
