import React, { createContext, useEffect, useState } from "react";

import {
  BrowserRouter,
  Navigate,
  Route,
  Routes,
  useNavigate,
} from "react-router-dom";
import Footer from "./components/Footer";
import Header from "./components/Header";
import About from "./Pages/About";
import Contact from "./Pages/Contact";
import Home from "./Pages/Home";
import api from "./api";
import Activity from "./Pages/Activity";
import SignUp from "./Pages/SignUp";
import Protected from "./components/Protected";
import LoginPage from "./Pages/LoginPage";

export const Global = createContext(null);
function App() {
  const empty = {
    name: "",
    description: "",
    date: "",
    duration: null,
    activityType: "",
  };
  const empty1 = {
    name: "",
    email: "",
    password: "",
  };
  const [err, setErr] = useState("");
  const [input, SetInput] = useState(empty);
  const [data, setData] = useState([]);
  const [select, setSelect] = useState("");
  const [login, setLogin] = useState(false);
  const [input1, SetInput1] = useState(empty1);
  const [isInput, setIsInput] = useState(false);
  const [isSignup, setIsSignup] = useState(false);
  const [loggedUser, setLoggedUser] = useState([]);

  const fetchapi = async () => {
    // var apidata = await fetch("http://localhost:4000/show");
    // apidata = await apidata.json();
    // setData(apidata);
    if (loggedUser) {
      const user = loggedUser;

      var data2 = await fetch("http://localhost:4000/getactivities", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(user),
      });
      data2 = await data2.json();
      setData(data2);
    }
  };

  useEffect(() => {
    fetchapi();
  }, [loggedUser]);

  const HandleLogin = async (event) => {
    event.preventDefault();

    const data1 = input1;
    var response = await fetch("http://localhost:4000/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data1),
    });

    if (response.status == 200) {
      response = await response.json();

      setLoggedUser(response);

      console.log(response.name);
      setLogin(true);
      setErr("");
    } else if (response.status == 401) {
      setErr("No User Found");
    } else if (response.status == 403) {
      setErr("Incorrect Password");
    }
    setTimeout(() => {
      setErr("");
    }, 3000);
  };
  const HandleLogout = async (event) => {
    event.preventDefault();

    setLogin(false);
    setIsSignup(false);
    setErr("");
    setLoggedUser(empty1);
  };
  const HandleSignup = async (event) => {
    event.preventDefault();

    const data1 = input1;

    const response = await fetch("http://localhost:4000/signup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data1),
    });
    if (response.status == 401) {
      console.log(response);
      setErr("Email Already Exists");
    } else {
      setErr("");
      setIsSignup(true);
    }
    setTimeout(() => {
      setErr("");
    }, 2000);
  };
  const HandleInput = (event) => {
    const { name, value } = event.target;
    SetInput({ ...input, [name]: value });
    console.log(name, value);
  };

  const HandleInput1 = (event) => {
    const { name, value } = event.target;
    SetInput1({ ...input1, [name]: value });
    console.log(name, value);
  };
  const HandleIsInput = () => {
    setIsInput(true);
  };
  const HandleDelete = async (deleteitem) => {
    await fetch(`http://localhost:4000/delete/${deleteitem}`, {
      method: "Delete",
    });
    await fetchapi();
  };
  const HandleEdit = (item, newDate) => {
    setIsInput(true);
    SetInput({ ...item, date: newDate });
  };
  const HandleSubmit = async (e, _id) => {
    e.preventDefault();
    const edit = data.find((item) => {
      return item._id === _id;
    });
    if (edit) {
      const { name, description, duration, date, activityType } = input;
      const data1 = { name, description, duration, date, activityType };
      await fetch(`http://localhost:4000/update/${_id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data1),
      });
    } else {
      if (loggedUser) {
        const data1 = { ...input, user: loggedUser._id };
        await fetch("http://localhost:4000/useractivity", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(data1),
        });
      }
    }
    await fetchapi();
    SetInput(empty);
    setIsInput(false);
  };
  console.log(data);
  console.log(loggedUser._id);
  return (
    <div className="Home container-fluid">
      <Global.Provider
        value={{
          data: data,
          input: input,
          input1: input1,
          isInput: isInput,
          login: login,
          isSignup: isSignup,
          err,
          loggedUser,
          HandleInput: HandleInput,
          HandleIsInput: HandleIsInput,
          HandleSubmit: HandleSubmit,
          HandleDelete: HandleDelete,
          HandleEdit: HandleEdit,
          HandleInput1: HandleInput1,
          HandleSignup: HandleSignup,
          HandleLogin: HandleLogin,
          HandleLogout,
          setLoggedUser,
        }}
      >
        <BrowserRouter>
          <Header />
          <Routes>
            <Route path="/" element={<Home />}></Route>
            <Route path="/about" element={<About />}></Route>
            <Route
              path="/activity"
              element={<Protected Component={Activity} />}
            ></Route>
            <Route path="/contact" element={<Contact />}></Route>
            <Route path="/login" element={<LoginPage />}></Route>
            <Route path="/signup" element={<SignUp />}></Route>

            <Route path="/" element={<Home />}></Route>
          </Routes>

          <Footer />
        </BrowserRouter>
      </Global.Provider>
    </div>
  );
}

export default App;
