import React, { createContext, useEffect, useState } from "react";

import { BrowserRouter, Route, Routes } from "react-router-dom";
import Footer from "./components/Footer";
import Header from "./components/Header";
import About from "./Pages/About";
import Contact from "./Pages/Contact";
import Home from "./Pages/Home";
import api from "./api";
import Activity from "./Pages/Activity";
import SignUp from "./Pages/SignUp";

export const Global = createContext(null);
function App() {
  const empty = {
    name: "",
    description: "",
    date: "",
    duration: null,
    activityType: "",
  };

  const [input, SetInput] = useState(empty);
  const [data, setData] = useState([]);
  const [select, setSelect] = useState("");
  const [isInput, setIsInput] = useState(false);
  const fetchapi = async () => {
    var apidata = await fetch("http://localhost:4000/show");
    apidata = await apidata.json();
    setData(apidata);
  };

  useEffect(() => {
    fetchapi();
  }, []);

  const HandleSignup = (event) => {
    event.preventDefault();
  };
  const HandleInput = (event) => {
    const { name, value } = event.target;
    SetInput({ ...input, [name]: value });
    console.log(name, value);
  };
  const HandleIsInput = () => {
    setIsInput(true);
  };
  const HandleDelete = async (deleteitem) => {
    await fetch(`http://localhost:4000/delete/${deleteitem}`, {
      method: "Delete",
    });
    await fetchapi();
  };
  const HandleEdit = (item, newDate) => {
    setIsInput(true);
    SetInput({ ...item, date: newDate });
  };
  const HandleSubmit = async (e, _id) => {
    e.preventDefault();
    const edit = data.find((item) => {
      return item._id === _id;
    });
    if (edit) {
      const { name, description, duration, date, activityType } = input;
      const data1 = { name, description, duration, date, activityType };
      await fetch(`http://localhost:4000/update/${_id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data1),
      });
    } else {
      const data1 = input;
      await fetch("http://localhost:4000/create", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data1),
      });
    }
    await fetchapi();
    SetInput(empty);
    setIsInput(false);
  };
  console.log(data);
  return (
    <div className="Home container-fluid">
      <Global.Provider
        value={{
          data: data,
          input: input,
          isInput: isInput,
          HandleInput: HandleInput,
          HandleIsInput: HandleIsInput,
          HandleSubmit: HandleSubmit,
          HandleDelete: HandleDelete,
          HandleEdit: HandleEdit,
        }}
      >
        <BrowserRouter>
          <Header />
          <Routes>
            <Route path="/" element={<Home />}></Route>
            <Route path="/about" element={<About />}></Route>
            <Route path="/activity" element={<Activity />}></Route>
            <Route path="/contact" element={<Contact />}></Route>
            <Route path="/signup" element={<SignUp />}></Route>
          </Routes>

          <Footer />
        </BrowserRouter>
      </Global.Provider>
    </div>
  );
}

export default App;
