import React, { createContext, useEffect, useState } from "react";

import { BrowserRouter, Route, Routes } from "react-router-dom";
import Footer from "./components/Footer";
import Header from "./components/Header";
import About from "./Pages/About";
import Contact from "./Pages/Contact";
import Home from "./Pages/Home";
import api from "./api";
import Activity from "./Pages/Activity";

export const Global = createContext(null);
function App() {
  const empty = {
    name: "",
    description: "",
    date: "",
    duration: null,
    activityType: "",
  };

  const [input, SetInput] = useState(empty);
  const [data, setData] = useState([]);
  const [select, setSelect] = useState("");
  const [isInput, setIsInput] = useState(false);
  const fetchapi = async () => {
    var apidata = await fetch("http://localhost:4000/show");
    apidata = await apidata.json();
    setData(apidata);
  };

  useEffect(() => {
    fetchapi();
  }, []);

  const HandleInput = (event) => {
    const { name, value } = event.target;
    SetInput({ ...input, [name]: value });
    console.log(name, value);
  };
  const HandleIsInput = () => {
    setIsInput(true);
  };
  const HandleDelete = (deleteitem) => {
    setData((item) => {
      return data.filter((item) => {
        return deleteitem.name !== item.name;
      });
    });
  };
  const HandleEdit = (item) => {
    setIsInput(true);
    SetInput(item);
  };
  const HandleSubmit = async (e) => {
    e.preventDefault();
    // const edit = data.find((item) => {
    //   return item.name === input.name;
    // });
    // if (edit) {
    //   setData(
    //     data.map((item) => {
    //       return item.name === edit.name
    //         ? {
    //             name: input.name,
    //             description: input.description,
    //             duration: input.duration,
    //             activityType: input.activityType,
    //             date: input.date,
    //           }
    //         : item;
    //     })
    //   );
    // } else {
    const data1 = input;
    await fetch("http://localhost:4000/create", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data1),
    });
    // setData([...data, input]);
    // }
    await fetchapi();
    SetInput(empty);
    setIsInput(false);
  };
  console.log(data);
  return (
    <div className="Home container-fluid">
      <Global.Provider
        value={{
          data: data,
          input: input,
          isInput: isInput,
          HandleInput: HandleInput,
          HandleIsInput: HandleIsInput,
          HandleSubmit: HandleSubmit,
          HandleDelete: HandleDelete,
          HandleEdit: HandleEdit,
        }}
      >
        <BrowserRouter>
          <Header />
          <Routes>
            <Route path="/" element={<Home />}></Route>
            <Route path="/about" element={<About />}></Route>
            <Route path="/activity" element={<Activity />}></Route>
            <Route path="/contact" element={<Contact />}></Route>
          </Routes>

          <Footer />
        </BrowserRouter>
      </Global.Provider>
    </div>
  );
}

export default App;
