import React, { useContext, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Global } from "../App";

function Login() {
  const navigate = useNavigate();
  const context = useContext(Global);
  useEffect(() => {
    if (context.login == true) {
      navigate("/");
    }
  });
  return (
    <div className="mb-5">
      <h1 className="text-white abouth1 text-center">Login Form</h1>
      <div className="container ">
        <div className="row">
          <div className="box">
            <form
              onSubmit={(event) => {
                context.HandleLogin(event);
              }}
            >
              <div className="input-container">
                <input
                  type="mail"
                  name="email"
                  required
                  onChange={context.HandleInput1}
                  value={context.input1.email}
                />
                <label>Email</label>
              </div>
              <div className="input-container">
                <input
                  name="password"
                  type="password"
                  required
                  onChange={context.HandleInput1}
                  value={context.input1.password}
                />
                <label>Password</label>
              </div>
              <button type="submit" className="btn">
                Login
              </button>
              <br />
            </form>{" "}
            <span className="lead pr-2 ">Don't Have account</span>
            <Link className="navbar-nav" to="/signup">
              Sign Up
            </Link>
          </div>
        </div>
        <br />
      </div>
    </div>
  );
}

export default Login;
